import React, { useState, useEffect } from "react";
import '../styles/app.css';
import io from 'socket.io-client';
const socket = io('http://localhost:3001');

const App = (props) => {
    const [inputValue, setInputValue] = useState('')
    const [messageHistory, setMessageHistory] = useState([])

    const inputMessage = createRef()
    const submitButton = createRef()

    useEffect(() => {
        socket.on('message', (message) => {
            onMessageReceived(message)
        })
    });


    // SOCKET FUNCTIONS
    function onMessageReceived(message) {
        updateMessageHistory(message)
    }

    function updateMessageHistory(message) {
        setMessageHistory([...messageHistory, message])
    }

    function sendMessage(message) {
        socket.emit('newMessage', message, (error) => {
            submitButton.removeAttribute('disabled')
            if (error) {
                return console.log(error)
            }
            console.log('The message was delivered')
            inputMessage.value = ''
            inputMessage.focus()
        })
    }

    // FORM
    function onInputChange(e) {
        setInputValue(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        sendMessage(inputValue)
    }


    function renderChatBox() {
        return messageHistory.map((message, index = 0) => {
            return <li key={index++}>{message}</li>
        })
    }

    return (
        <>
            <ul id="messages">{renderChatBox()}</ul>
            <form id="chatBox" onSubmit={handleSubmit}>
                <input ref={inputMessage} value={inputValue} onChange={onInputChange} id="inputChat" name="message" />
                <button ref={submitButton} id="submit" type="submit">Submit</button>
            </form>
            {/* <button id="send-location_button">Send Location</button> */}
        </>
    )
}

export default App;
