import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import '../styles/app.css'
import ChatBox from '../components/ChatBox';

const App = (props) => {

    const [response, setResponse] = useState(false);

    useEffect(() => {
        const socket = io('http://localhost:3001')
        /* socket.on("FromAPI", data => this.setState({ response: data })); */
    });


    function handleSubmit(e) {
        e.preventDefault()
        socket.emit('newMessage', e.target.elements.message.value, (error) => {
            $chatBoxButton.removeAttribute('disabled')
            $chatBoxInput.value = ''
            $chatBoxInput.focus()

            if (error) {
                return console.log(error)
            }
            console.log('The message was delivered')
        })
    }
    return (
        <>
            <ul id="messages"></ul>
            <form id="chatBox" onSubmit={handleSubmit}>
                <input id="inputChat" name="message" />
                <button id="submit" type="submit">Submit</button>
            </form>
            <button id="send-location_button">Send Location</button>
        </>
    )
}

export default App;
