import React, { useState, useEffect } from "react";
import '../styles/app.css';
import io from 'socket.io-client';
const socket = io('http://localhost:3001');

const App = (props) => {


    useEffect(() => {

    });

    const $chatBox = document.getElementById('chatBox')
    const $chatMessages = document.getElementById('messages')
    const $chatBoxButton = document.getElementById('submit')
    const $chatBoxInput = document.querySelector('input')
    const $locationButton = document.getElementById('send-location_button')

    function handleSubmit(e) {
        e.preventDefault()
        sendMessage($chatBoxInput.value)
    }

    function sendMessage(message) {
        socket.emit('newMessage', message, (error) => {
            $chatBoxButton.removeAttribute('disabled')
            if (error) {
                return console.log(error)
            }
            console.log('The message was delivered')
            $chatBoxInput.value = ''
            $chatBoxInput.focus()
        })
    }

    return (
        <>
            <ul id="messages"></ul>
            <form id="chatBox" onSubmit={handleSubmit}>
                <input id="inputChat" name="message" />
                <button id="submit" type="submit">Submit</button>
            </form>
            {/* <button id="send-location_button">Send Location</button> */}
        </>
    )
}

export default App;
