import React from 'react';

import socket from './socket';
import userContext from './userContext';
import GameContainerSideBar from './GamesComponents/GameContainerSideBar';
import TicTacToe from './GamesComponents/TicTacToe';

const GameContainer = (props) => {
    function onJoinRoomClick() {
        console.log(props);
        /* socket.emit('joinGameRoom', { username, room }, (error) => {
            if (error) {
                alert(error);
                window.location.href = '/';
            }
        }); */
    }

    return (
        <div id="game__container" className="game__container">
            <GameContainerSideBar />
            <userContext.Consumer>
                {(user) => (
                    <button onClick={() => onJoinRoomClick(user)}>JOIN</button>
                )}
            </userContext.Consumer>

            <TicTacToe socket={props.socket} />
        </div>
    );
};

export default GameContainer;
