import React from 'react';
import { Link } from 'react-router-dom';

const LoginPage = (props) => {
    const [inputValue, setInputValue] = useState({
        username: '',
        room: ''
    })

    // FORM
    function onInputChange(e) {
        setInputValue(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
    }


    return (
        <div className="centered-form">
            <div className="centered-form__box">
                <h1>Join</h1>
                <form>
                    <label>Display Name</label>
                    <input type="text" name="username" placeholder="Display name" value={this.state.value} onChange={this.handleChange} required></input>
                    <label>Room</label>
                    <input type="text" name="room" placeholder="Room" value={this.state.value} onChange={this.handleChange} required></input>
                    <button type="submit">
                        <Link to={{
                            pathname: "/chatroom",
                            state: {
                                username,
                                room
                            }
                        }}>Join</Link>
                    </button>
                </form>
            </div>
        </div >
    )
}

export default LoginPage
