import React, { useState, useEffect, useRef } from "react";
import { Router, Route } from 'react-router-dom';
import '../styles/app.css';
import io from 'socket.io-client';
import Moment from 'react-moment';

import history from '../history';
import LoginPage from './LoginPage';

const socket = io('http://localhost:3001');

const App = (props) => {
    // Create a state for the input element's values
    // Create a state containing the message history 
    const [inputValue, setInputValue] = useState('')
    const [messageHistory, setMessageHistory] = useState([])

    // Create a reference for the input and submit button elements 
    const inputMessage = useRef(null)
    const submitButton = useRef(null)

    // Upon mounting, check for message received from the server
    useEffect(() => {
        socket.on('message', (messageObject) => {
            onMessageReceived(messageObject)
        })
    })

    // SOCKET FUNCTIONS
    function onMessageReceived(messageObject) {
        updateMessageHistory(messageObject)
    }

    function updateMessageHistory(messageObject) {
        // Add message object containing the text, creation date, and username of sender
        setMessageHistory([...messageHistory, messageObject])
    }

    function sendMessage(message) {
        socket.emit('newMessage', message, (error) => {
            submitButton.current.removeAttribute('disabled')
            if (error) {
                return console.log(error)
            }
            console.log('The message was delivered')
            inputMessage.current.value = ''
            inputMessage.current.focus()
        })
    }

    // FORM
    function onInputChange(e) {
        setInputValue(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        submitButton.current.setAttribute('disabled', 'disabled')
        sendMessage(inputValue)
    }

    function renderChatBox() {
        return messageHistory.map((message, index = 0) => {
            return (
                <div className="message" key={index++}>
                    <p>
                        <span className="message__name">{message.username}</span>
                        <span className="message__meta">{<Moment date={message.createdAt} format="h:mm a" />}</span>
                    </p>
                    <p>{message.text}</p>
                </div>
            )
        })
    }

    return (
        <>
            <Router history={history}>
                <Route path="/" exact component={LoginPage} />
            </Router>
        </>
        /* <ul id="chat__messages">{renderChatBox()}</ul>
        <form id="chatBox" onSubmit={handleSubmit}>
            <input ref={inputMessage} value={inputValue} onChange={onInputChange} id="inputChat" name="message" />
            <button ref={submitButton} id="submit" type="submit">Submit</button>
        </form> */
    )
}

export default App;
