        <>
            <div className="TicTacToe__container">
                <div className="chooseButtons__container">
                    <button
                        type="chooseButton"
                        onClick={() => onSymbolSelect('X')}
                    >
                        X
                    </button>
                    <div>OR</div>
                    <button
                        type="chooseButton"
                        onClick={() => onSymbolSelect('O')}
                    >
                        O
                    </button>
                </div>
                <div className="gameButtons">{renderButtons()}</div>
            </div>
        </>
    );
};

export default TicTacToe;
